mapper.insert:
INSERT INTO recipe_like (
    recipe_id,
    user_id
)
VALUES (
    #{recipeId},
    #{userId}
)

mapper.delete:
DELETE FROM recipe_like
WHERE recipe_id = #{recipeId}
  AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM recipe_like
WHERE recipe_id = #{recipeId}
  AND user_id = #{userId}

SELECT COUNT(*)
FROM recipe_like
WHERE recipe_id = #{recipeId}
