mapper.update:
UPDATE user_recipe
SET
    title = #{title},
    description = #{description},
    ingredients = #{ingredients},
    cooking_steps = #{cookingSteps},
    preparation_time = #{preparationTime},
    cooking_time = #{cookingTime},
    servings = #{servings},
    difficulty_level = #{difficultyLevel}

        , main_image_path = #{mainImagePath}


        , main_image_data = #{mainImageData}
        , main_image_type = #{mainImageType}

WHERE id = #{id}

UPDATE user_recipe
SET view_count = view_count + 1
WHERE id = #{id}

UPDATE user_recipe
SET like_count = (SELECT COUNT(*) FROM recipe_like WHERE recipe_id = #{recipeId})
WHERE id = #{recipeId}

UPDATE user_recipe
SET comment_count = (SELECT COUNT(*) FROM recipe_comment WHERE recipe_id = #{recipeId})
WHERE id = #{recipeId}

mapper.select:
SELECT COUNT(*) > 0
FROM user_recipe
WHERE rcp_seq = #{rcpSeq}

SELECT id, source, rcp_seq, title, rcp_way2, rcp_pat2, info_eng, att_file_no_main, att_file_no_mk, parsed_ingredients, ingredient_count, created_at FROM user_recipe WHERE source = 'api'
AND rcp_way2 = #{rcpWay2}
AND rcp_pat2 = #{rcpPat2}
AND title LIKE CONCAT('%', #{searchRecipeName}, '%')
AND rcp_parts_dtls LIKE CONCAT('%', #{searchIngredient}, '%')
ORDER BY created_at DESC

SELECT id, source, rcp_seq, title, rcp_way2, rcp_pat2, info_eng, att_file_no_main, att_file_no_mk, parsed_ingredients, ingredient_count, created_at FROM user_recipe WHERE source = 'api'
AND rcp_way2 = #{rcpWay2}
AND rcp_pat2 = #{rcpPat2}
AND title LIKE CONCAT('%', #{searchRecipeName}, '%')
AND rcp_parts_dtls LIKE CONCAT('%', #{searchIngredient}, '%')
AND (
parsed_ingredients LIKE CONCAT('%', #{ingredient}, '%')
)
ORDER BY created_at DESC
