mapper.select:
SELECT ur.id, ur.source, ur.rcp_seq, ur.title, ur.rcp_way2, ur.rcp_pat2, ur.info_eng, ur.att_file_no_main, ur.att_file_no_mk, ur.parsed_ingredients, ur.ingredient_count, ur.created_at, COALESCE(urm.matched_count, 0) AS matchedIngredientCount, COALESCE(urm.total_count, ur.ingredient_count, 0) AS totalIngredientCount, COALESCE(urm.match_percent, 0) AS matchScore, urm.matched_ingredients AS matchedIngredients FROM user_recipe ur LEFT JOIN user_recipe_match urm ON ur.id = urm.recipe_id AND urm.user_id = #{userId} WHERE ur.source = 'api'
AND ur.rcp_way2 = #{rcpWay2}
AND ur.rcp_pat2 = #{rcpPat2}
AND ur.title LIKE CONCAT('%', #{searchRecipeName}, '%')
AND ur.rcp_parts_dtls LIKE CONCAT('%', #{searchIngredient}, '%')
ORDER BY COALESCE(urm.match_percent, 0) DESC, urm.matched_count DESC, ur.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM user_recipe ur WHERE ur.source = 'api'
AND ur.rcp_way2 = #{rcpWay2}
AND ur.rcp_pat2 = #{rcpPat2}
AND ur.title LIKE CONCAT('%', #{searchRecipeName}, '%')
AND ur.rcp_parts_dtls LIKE CONCAT('%', #{searchIngredient}, '%')
